const initialState = 0

// Reducer takes two values state and action
export const bankingReducer = (state = initialState, action) => {
    switch (action.type) {
      case "DEPOSITE":
        return state + action.amount
      case "WITHDRAW":
        return state - action.amount
      case "COLLECT_INTEREST":
          return state*1.03
      case "DELETE_ACCOUNT":
        return 0
      default:
        return state; // return  the old state
    }
  };

  // actions

  // deposit
  const deposit = {
    type: "DEPOSIT",
    amount: 20,
  };

  // withdraw
  const withdraw = {
    type: "WITHDRAW",
    amount: 30,
  };

  // Collect interest
  const collectInterest = {
    type: "COLLECT_INTEREST",
  };

  // delete account

  const deleteAccount = {
    type: "DELETE_ACCOUNT",
  };
