import React from 'react'
import {useSelector, useDispatch } from "react-redux"  
import {login, logout} from '../Redux/actions/authActions'


const Auth = () => {

    const dispatch = useDispatch()
    const isAuth = useSelector(state => state.auth)
    const [auth, setAuth] = React.useState(isAuth)

    const handleLogin = () => login(dispatch())
    const handleLogout = () => login(dispatch())

    return (
        <div>
            <button className="btn btn-info" onClick={handleLogin}>Login</button>
            <button className="btn btn-info" onClick={handleLogout}>Login</button>
        </div>
    )
}

export default Auth
